import React, { useRef } from "react";
import Section from "./Section";
export default function Sidebar(props) {
  const sidebarList = [
    {
      id: 1,
      title: "Installation",
      url: "#installation",
    },
    {
      id: 2,
      title: "Banner Component",
      url: "#bannerComponent",
    },
    {
      id: 3,
      title: "Button Component",
      url: "#buttonComponent",
    },
  ];

  const scrollDown = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth",
    });
  };

  const listItem = sidebarList.map((items) => (
    <li key={props.id}>
      <a
        className={Section[items.url] !== items.url ? "item-link" : "active"}
        href={items.url}
        onClick={() => scrollDown(items.url)}
      >
        {items.title}
      </a>
    </li>
  ));
  return (
    <>
      <aside>
        <div className="sidebar">
          <ul>{listItem}</ul>
        </div>
      </aside>
    </>
  );
}
