const Button = ({
  color = "primary",
  size = "medium",
  children,
  type = "button",
  style,
  radius = "6px",
  onClick,
}) => {
  // const ButtonClass = `btn` + (props.className ? ` ${props.className}` : "");
  // const ButtonType = props.type ? `${props.type}` : "button";

  // const bgColor = style === "filled" ? "btn-primary" : "transparent";
  // const btnSize = size === "large" ? "btn-large" : "btn-small";

  return (
    <>
      <button
        {...children}
        type={type}
        style={style + radius}
        onClick={onClick}
        className={`btn btn-${color} btn-${size}`}
        // onClick={props.clickHandler}
      >
        <span>{children}</span>
      </button>
    </>
  );
};

export default Button;
