export default function Button(props) {
  const ButtonClass = "btn" + (props.className ? ` ${props.className}` : "");
  const ButtonType = props.type ? `${props.type}` : "button";

  const bgColor = props.style === "filled" ? "btn-primary" : "transparent";
  const btnSize = props.size === "large" ? "btn-large" : "btn-small";

  return (
    <>
      <button
        {...props}
        type={ButtonType}
        className={ButtonClass}
        onClick={props.clickHandler}
      >
        <span>{props.children}</span>
      </button>
    </>
  );
}
